import{_ as e,o as r,c as o,h as i}from"./app.42f39072.js";const p=JSON.parse('{"title":"Firmware Guide","description":"","frontmatter":{},"headers":[{"level":2,"title":"SETUP","slug":"setup","link":"#setup","children":[{"level":3,"title":"Micropython","slug":"micropython","link":"#micropython","children":[]},{"level":3,"title":"Arduino Core","slug":"arduino-core","link":"#arduino-core","children":[]}]}],"relativePath":"firmware_guide/firmware.md","lastUpdated":1676228286000}'),a={name:"firmware_guide/firmware.md"},t=i('<h1 class="text-[var(--font-accent)]" id="firmware-guide" tabindex="-1">Firmware Guide <a class="header-anchor" href="#firmware-guide" aria-hidden="true">#</a></h1><p>This project has two different firmware options, one for the Arduino Core and one for Micropython. The Arduino Core is more feature rich, but the Micropython version is more stable. If you are looking for a more feature rich firmware, please check out the <a href="#arduino-core">Arduino Core</a> version.</p><h2 id="setup" tabindex="-1"><em><strong>SETUP</strong></em> <a class="header-anchor" href="#setup" aria-hidden="true">#</a></h2><hr><h3 id="micropython" tabindex="-1">Micropython <a class="header-anchor" href="#micropython" aria-hidden="true">#</a></h3><div class="danger custom-block"><p class="custom-block-title">Warning</p><p>This version of the firmware is stable, but it lacks alot of features and is no longer being developed. If you are looking for a more feature rich firmware, please check out the <a href="#arduino-core">Arduino Core</a> version.</p></div><ol><li>Plug ESP32 into computer - open your IDE (i used Thonny for this project) Flash the micropython firmware to the esp32 <ol><li>Upload ESP32MicropytyhonLibraries ---&gt; rename to ESP32Micro</li><li>Upload <a href="http://boot.py" target="_blank" rel="noreferrer">boot.py</a> and <a href="http://greenhouse.py" target="_blank" rel="noreferrer">greenhouse.py</a> to root directory</li></ol></li><li>Navigate to <a href="/ESP32GreenHouseTowerDIY-Docs/firmware_guide/mdns">MDNS</a> to see how to configure your MQTT broker</li></ol><hr><h3 id="arduino-core" tabindex="-1">Arduino Core <a class="header-anchor" href="#arduino-core" aria-hidden="true">#</a></h3><ol><li>Navigate to <a href="/ESP32GreenHouseTowerDIY-Docs/firmware_guide/configure_firmware">Configure Firmware</a> to see how to configure your firmware</li><li>Plug ESP32 into computer - navigate to <a href="/ESP32GreenHouseTowerDIY-Docs/firmware_guide/web_serial">WebSerial</a><ol><li>Configure your chosen sensors and features</li><li>Press build and upload, and wait for the firmware to upload, this may take a few minutes.</li><li>After upload, open the serial monitor, click the <code>Reboot</code> button, and wait for the firmware to reboot.</li><li>Now, you can send commands to the firmware using the serial monitor. As well as view the serial output from the firmware.</li></ol></li><li>Navigate to <a href="/ESP32GreenHouseTowerDIY-Docs/firmware_guide/mdns">MDNS</a> to see how to configure your MQTT broker</li></ol>',10),n=[t];function s(l,h,d,c,u,f){return r(),o("div",null,n)}const w=e(a,[["render",s]]);export{p as __pageData,w as default};
